BEGIN TRANSACTION;
CREATE TABLE "cinemas" ("id" integer PRIMARY KEY AUTOINCREMENT NOT NULL, "name" varchar(255), "phone" varchar(255), "address" text, "active" boolean, "created_at" datetime NOT NULL, "updated_at" datetime NOT NULL);
CREATE TABLE "cinemas_movies" ("id" integer PRIMARY KEY AUTOINCREMENT NOT NULL, "movie_id" integer, "cinema_id" integer, "active" boolean, "created_at" datetime NOT NULL, "updated_at" datetime NOT NULL, "mon" varchar(255), "tue" varchar(255), "wed" varchar(255), "thu" varchar(255), "fri" varchar(255), "sat" varchar(255), "sun" varchar(255));
CREATE TABLE "movies" ("id" integer PRIMARY KEY AUTOINCREMENT NOT NULL, "title" varchar(255), "rating" integer, "director" varchar(255), "cast" text, "details" text, "reviews" text, "active" boolean, "created_at" datetime NOT NULL, "updated_at" datetime NOT NULL);
CREATE TABLE "schema_migrations" ("version" varchar(255) NOT NULL);
CREATE TABLE sqlite_sequence(name,seq);
CREATE UNIQUE INDEX "unique_schema_migrations" ON "schema_migrations" ("version");
COMMIT;
